package br.com.readfile;import br.com.readfile.abstracts.ReadFile;import br.com.readfile.abstracts.ReaderExcel;import br.com.readfile.entities.ReadExcel;import br.com.readfile.entities.User;import br.com.readfile.entities.WorkMap;import br.com.readfile.imp.FastExcelReaderImp;import java.io.FileNotFoundException;import java.io.IOException;import java.util.List;public class Main {  public static void main(String[] args) {    ReaderExcel reader = new FastExcelReaderImp();    WorkMap workMap = new WorkMap();    workMap.put("id", "id");    workMap.put("titulo", "title");    workMap.put("author", "name");    ReadFile readFile = new ReadExcel(User.class, workMap, reader);    String filePath = "src/main/resources/Examplex.xlsx";    try {      List<User> user = readFile.read(filePath);      for(var u : user) {        u.show();      }    }    catch (FileNotFoundException e) {      Log.error("File not found");    } catch (IOException e) {      Log.error(e.getMessage());    }  }}