package br.com.readfile;import java.util.List;public class Main {  public static void main(String[] args) {    ReaderExcel reader = new FastExcelReaderImp();    WorkMap workMap = new WorkMap();    workMap.put("id", "id");    workMap.put("titulo", "title");    workMap.put("author", "name");    ReadFile readFile = new ReadExcel(User.class, workMap, reader);    String filePath = "src/main/resources/Example.xlsx";    List<User> user = readFile.read(filePath);    for(var u : user) {      u.show();    }  }}